mapper.select:
SELECT a.we_wiki_idx AS idx /* MainManage.getWikiList - 대시보드의 위키 목록을 지정된 건수만큼 조회해온다. */ , a.we_wiki_title AS title , a.we_ins_date AS insDate , IFNULL(d.we_wiki_idx, 0) AS favorited , b.we_view_privacy , c.we_user_nick , a.we_space_idx , a.we_ins_user , DATE_FORMAT(a.we_ins_date, '%Y-%m-%d') AS insert_date FROM we_wiki a INNER JOIN we_space b ON a.we_space_idx=b.we_space_idx INNER JOIN we_user c ON a.we_ins_user = c.we_user_idx LEFT JOIN (SELECT we_wiki_idx,we_space_idx FROM we_favorite WHERE we_user_idx=#{userIdx} AND we_favorite_type='WIKI' AND we_use_yn='Y') d ON a.we_space_idx = d.we_space_idx AND a.we_wiki_idx = d.we_wiki_idx WHERE a.WE_USE_YN = 'Y' GROUP BY a.we_wiki_title , a.we_ins_date
ORDER BY a.we_ins_date DESC LIMIT #{startRow}, #{endRow}
ORDER BY a.we_wiki_title LIMIT #{startRow}, #{endRow}

SELECT we_wiki_idx , we_ins_date , we_wiki_text , we_wiki_title , profileImg , we_user_nick , we_user_idx , DATE_FORMAT(we_ins_date, '%Y-%m-%d') AS insert_date FROM ( SELECT MAX(a.we_wiki_idx) AS we_wiki_idx , MAX(a.we_ins_date) AS we_ins_date , b.we_wiki_text , b.we_wiki_title , CONCAT( c.we_thumb_path, c.we_thumb_name) AS profileImg , d.we_user_nick , d.we_user_idx FROM we_wiki_log a INNER JOIN we_wiki b ON a.we_wiki_idx = b.we_wiki_idx and b.WE_USE_YN = 'Y' INNER JOIN we_profile c ON a.we_user_idx=c.we_user_idx AND b.we_ins_user=c.we_user_idx INNER JOIN we_user d ON a.we_user_idx=d.we_user_idx AND b.we_ins_user=d.we_user_idx AND c.we_user_idx=d.we_user_idx WHERE a.we_wiki_status='S'
AND b.we_space_idx=#{spaceIdx}
GROUP BY we_wiki_text, we_wiki_title, profileImg, we_user_nick, we_user_idx ) T1 ORDER BY we_ins_date DESC LIMIT #{startRow}, #{endRow}

SELECT COUNT(*) AS CNT
FROM we_wiki
WHERE we_ins_user=#{userIdx}
