mapper.select:
SELECT /* SpaceManage.getGroupParticipant - 해당공간의 그룹참여자를 가져온다. */ c.we_group_idx AS idx , c.we_group_name AS name FROM we_space a, we_space_group b, we_group_info c WHERE a.we_space_idx = b.we_space_idx AND b.we_group_idx = c.we_group_idx AND a.we_space_idx = #{spaceIdx,jdbcType=NUMERIC}
AND b.we_view_permit = 1 AND b.we_insert_permit = 0 AND b.we_edit_permit = 0
AND b.we_view_permit = 0 AND b.we_insert_permit = 1 AND b.we_edit_permit = 1

SELECT /* SpaceManage.getUserParticipant - 해당공간의 구성원참여자를 가져온다. */ c.we_user_idx AS idx , c.we_user_name AS name FROM we_space a, we_space_user b, we_user c WHERE a.we_space_idx = b.we_space_idx AND b.we_user_idx = c.we_user_idx AND a.we_space_idx = #{spaceIdx,jdbcType=NUMERIC}
AND b.we_view_permit = 1 AND b.we_insert_permit = 0 AND b.we_edit_permit = 0
AND b.we_view_permit = 0 AND b.we_insert_permit = 1 AND b.we_edit_permit = 1

SELECT a.we_group_idx , b.we_user_idx FROM we_space_group a INNER JOIN we_group_user b ON a.we_group_idx=b.we_group_idx WHERE a.we_space_idx=#{spaceIdx} and b.we_user_idx =#{userIdx}
AND a.we_view_permit = 1 AND a.we_insert_permit = 0 AND a.we_edit_permit = 0
AND a.we_view_permit = 0 AND a.we_insert_permit = 1 AND a.we_edit_permit = 1

SELECT COUNT(we_user_idx) FROM we_space_user WHERE we_space_idx=#{spaceIdx} and we_user_idx =#{userIdx}
AND we_view_permit = 1 AND we_insert_permit = 0 AND we_edit_permit = 0
AND we_view_permit = 0 AND we_insert_permit = 1 AND we_edit_permit = 1

mapper.update:
UPDATE we_space_join
SET we_join_status = 'REJECT'
WHERE we_space_join_idx = #{spaceJoinIdx}

UPDATE we_space_join
SET we_join_status = 'APPROVE'
WHERE we_space_join_idx = #{spaceJoinIdx}
